mapper.select:
SELECT p.id AS id, -- 게시글 번호 p.title AS title, -- 제목 p.created_at AS createdAt, -- 작성일 p.updated_at AS updatedAt, -- 수정일 p.view AS view, -- 조회수 sh.company_name AS shelterName, -- 보호소 이름 sh.ceo_name AS userName -- 보호소장 이름 FROM donationPost p LEFT JOIN shelter_head sh ON p.head_id = sh.head_id
WHERE ( p.title LIKE CONCAT('%', #{keyword}, '%') OR sh.company_name LIKE CONCAT('%', #{keyword}, '%') OR sh.ceo_name LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY p.created_at DESC LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE donationPost
SET view = view + 1
WHERE id = #{id}
